export default function ChangeCarOwner() {
    const classes = useStyles();

    //States
    const [carID, setCarID] = React.useState('')
    const [newOwner, setNewOwner] = React.useState('')
    const [hash, setHash] = React.useState('')
    const [firstValid, setfirstValid] = React.useState('')
    const [lastValid, setLastValid] = React.useState('')
    const [receiver, setReceiver] = React.useState('')
    //State Handlers
    const handleCarID = (event) => {
        setCarID(event.target.value)
    }

    const handleNewOwner = (event) => {
        setNewOwner(event.target.value)
    }

    const handlePassPhrase = (event) => {
        setPassPhrase(event.target.value)
    }

    const handleSubmit = async () => {
        try{
            if (typeof window.AlgoSigner !== 'undefined') {
                console.log("algosigner installed");
              } else {
                console.log("algosigner not installed");
              }
              window.AlgoSigner.connect()
              .then((d) => {
                console.log("Connect")
              })
              .catch((e) => {
                console.error(e);
              });
            getTestNetPrimaryAccount();
            // get node suggested parameters
            let params = await algodClient.getTransactionParams().do();
            // comment out the next two lines to use suggested fee
            params.fee = 1000;
            params.flatFee = true;
            let appArgs = [];
            appArgs.push(new Uint8Array(Buffer.from("changeOwner")));
            //appArgs.push(new Uint8Array(Buffer.from(newOwner)));
            // create unsigned transaction   [str:addCar, str:Skoda, str: ]
            //let state= await client.accountInformation(account.addr).do()
            //let txn = algosdk.makeApplicationOptInTxn(sender, params, index);
            let txn = algosdk.makeApplicationNoOpTxn(account.address, params, appIndex, appArgs,[carID,newOwner]);
            let txId = txn.txID().toString();

            // Sign the transaction
            let base64Tx = window.AlgoSigner.encoding.msgpackToBase64(txn.toByte());

            let signedTxs = await window.AlgoSigner.signTxn([
             {
                txn: base64Tx,
             },
            ]);

            // Get the base64 encoded signed transaction and convert it to binary
           let signedTxn = window.AlgoSigner.encoding.base64ToMsgpack(signedTxs[0].blob); 
            console.log("Signed transaction with txID: %s", txId);

            // Submit the transaction
            await algodClient.sendRawTransaction(signedTxn).do();

            // Wait for confirmation
            await waitForConfirmation(algodClient, txId);

            // display results
            let transactionResponse = await algodClient.pendingTransactionInformation(txId).do();
            console.log("Called app-id:",transactionResponse['txn']['txn']['apid'])
            if (transactionResponse['global-state-delta'] !== undefined ) {
                console.log("Global State updated:",transactionResponse['global-state-delta']);
            }
            if (transactionResponse['local-state-delta'] !== undefined ) {
                console.log("Local State updated:",transactionResponse['local-state-delta']);
            }
            }catch(error){
                console.log(`Failed: AddCar - ${error}`);
                return 0;
            }
    }

    const allData = () => {
        return {
            'carID': carID,
            'carOwner': newOwner,
            'passPhrase': passPhrase,
        }
    }

    return (
        <div>
            <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="CarID"
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                        </InputAdornment>
                    ),
                }}
                onInput={handleCarID}
            />
            <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="Secret Key"
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                        </InputAdornment>
                    ),
                }}
                onInput={handleNewOwner}
            />
            <Button onClick={handleSubmit}>
                Submit
            </Button>

            <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="Hash"
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                        </InputAdornment>
                    ),
                }}
                onInput={handleCarMake}
            />
            <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="First Valid Round"
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                        </InputAdornment>
                    ),
                }}
                onInput={handleCarModel}
            />
            <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="Last Valid Round"
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                        </InputAdornment>
                    ),
                }}
                onInput={handleCarColor}
            />
            <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="Receiver"
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                        </InputAdornment>
                    ),
                }}
                onInput={handleCarID}
            />
            <Button onClick={handleSubmit}>
                Submit
            </Button>
        </div>
    );
}
