import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import InputAdornment from '@material-ui/core/InputAdornment';

import TextField from '@material-ui/core/TextField';
import { Button } from '@material-ui/core'
import {toast} from 'react-toastify'

const algosdk = require('algosdk');

const crypto = require('crypto')

const useStyles = makeStyles((theme) => ({
    margin: {
        margin: theme.spacing(1),
    },
}));

const algodAddress = "https://testnet.algoexplorerapi.io/";
const algodToken = "";
const algodPort ="";

let algodClient = new algosdk.Algodv2(algodToken, algodAddress, algodPort);

var check =algodClient.healthCheck().do();
if (check){
    console.log("client connected")
}

function hashPassPhrase(passPhrase) {
    const hash = crypto.createHash('sha256')
    hash.update(passPhrase)
    return hash.digest('hex')
}

export default function AddCar() {
    const classes = useStyles();

    //States
    const [carID, setCarID] = React.useState('')
    const [carMake, setCarMake] = React.useState('')
    const [carModel, setCarModel] = React.useState('')
    const [carColor, setCarColor] = React.useState('')
//    const [carOwner, setCarOwner] = React.useState('')
//    const [passPhrase, setPassPhrase] = React.useState('')

    //Handlers
    const handleCarID = (event) => {
        setCarID(event.target.value)
    }

    const handleCarModel = (event) => {
        setCarModel(event.target.value)
    }

    const handleCarMake = (event) => {
        setCarMake(event.target.value)
    }

    const handleCarColor = (event) => {
        setCarColor(event.target.value)
    }

    // call application 
    async function callApp(client, index, appArgs) {
        // define sender
    try{
        var sender = account.addr;

        // get node suggested parameters
        let params = await client.getTransactionParams().do();
        // comment out the next two lines to use suggested fee
        params.fee = 1000;
        params.flatFee = true;

        // create unsigned transaction   [str:addCar, str:Skoda, str: ]
        //let state= await client.accountInformation(account.addr).do()
        //let txn = algosdk.makeApplicationOptInTxn(sender, params, index);
        let txn = algosdk.makeApplicationNoOpTxn(sender, params, index, appArgs)
        let txId = txn.txID().toString();

        // Sign the transaction
        let signedTxn = txn.signTxn(account.sk);
        console.log("Signed transaction with txID: %s", txId);

        // Submit the transaction
        await client.sendRawTransaction(signedTxn).do();

        // Wait for confirmation
        await waitForConfirmation(client, txId);

        // display results
        let transactionResponse = await client.pendingTransactionInformation(txId).do();
        console.log("Called app-id:",transactionResponse['txn']['txn']['apid'])
        if (transactionResponse['global-state-delta'] !== undefined ) {
            console.log("Global State updated:",transactionResponse['global-state-delta']);
        }
        if (transactionResponse['local-state-delta'] !== undefined ) {
            console.log("Local State updated:",transactionResponse['local-state-delta']);
        }
        }catch(error){
            console.log(`${RED}<-- Failed: AddCar - ${err}${RESET}`);
            return 0;
        }
    }

    const handleSubmit = async () => {
        try{
            let status = (await algodClient.status().do());
            console.log("Algorand network status: %o", status);
            let appArgs = [];
            appArgs.push(new Uint8Array(Buffer.from("addCar")));
            appArgs.push(new Uint8Array(Buffer.from(carID)));
            appArgs.push(new Uint8Array(Buffer.from(carMake)));
            appArgs.push(new Uint8Array(Buffer.from(carModel)));
            appArgs.push(new Uint8Array(Buffer.from(carColor)));
            let createCarEvent = await callApp(algodClient, 23658482, appArgs);
            if (createCarEvent != 0) {
                res.status(200).send({status: 200, message: `Transaction Submitted: Car Added for ${req.body.carOwner}`})
            } else {
                res.status(500).send({status: 500, message: `Error!!! Failed to add Car for ${req.body.carOwner}`})
            }
            }catch(error){
                console.error(`Failed to evaluate transaction: ${error}`);
                res.status(500).json({ error: error });
                process.exit(1);
            }
    }

    const allData = () => {
        return {
            'carID': carID,
            'carColor': carColor,
            'carModel': carModel,
            'carOwner': carOwner,
            'carMake': carMake,
            'passPhrase': passPhrase,
        }
    }

    return (
        <div>
            <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="CarID"
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                        </InputAdornment>
                    ),
                }}
                onInput={handleCarID}
            />
            <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="Car Make"
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                        </InputAdornment>
                    ),
                }}
                onInput={handleCarMake}
            />
            <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="Car Model"
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                        </InputAdornment>
                    ),
                }}
                onInput={handleCarModel}
            />
            <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="Car Color"
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                        </InputAdornment>
                    ),
                }}
                onInput={handleCarColor}
            />
            <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="Car Owner"
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                        </InputAdornment>
                    ),
                }}
                onInput={handleCarOwner}
            />
            {/* <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="Secret Pass Phrase"
                InputProps={{
                    startAdornment: (
                        <InputAdornment position="start">
                        </InputAdornment>
                    ),
                }}
                onInput={handlePassPhrase}
            /> */}
            <Button onClick={handleSubmit}>
                Submit
            </Button>
        </div>
    );
}
